mapper.select:
select   from t_dict


		and ID = #{Dict.id}


		and NAME = #{Dict.name}


		and DKEY = #{Dict.dkey}


		and DVAL = #{Dict.dval}


		and USE_FLAG = #{Dict.useFlag}


		and ORDER_NO = #{Dict.orderNo}



		and CREATE_DATE >= #{createDateBegin} 




		and CREATE_DATE <= #{createDateEnd}




	order by ${order} ${sort}

mapper.update:
update t_dict
NAME = #{name, jdbcType=VARCHAR}, DKEY = #{dkey, jdbcType=VARCHAR}, DVAL = #{dval, jdbcType=VARCHAR}, USE_FLAG = #{useFlag, jdbcType=NUMERIC}, ORDER_NO = #{orderNo, jdbcType=NUMERIC}, CREATE_DATE = #{createDate, jdbcType=TIMESTAMP}, ID = #{id}
ID = #{id}
