mapper.select:
select   from t_teacher


		and ID = #{Teacher.id}


		and NAME = #{Teacher.name}


		and SEX = #{Teacher.sex}



		and BIRTHDAY >= #{birthdayBegin} 




		and BIRTHDAY <= #{birthdayEnd}




		and WORK_DATE >= #{workDateBegin} 




		and WORK_DATE <= #{workDateEnd}



		and SPECIALTY = #{Teacher.specialty}


		and TLEVEL = #{Teacher.tlevel}



		and CREATE_DATE >= #{createDateBegin} 




		and CREATE_DATE <= #{createDateEnd}




	order by ${order} ${sort}

mapper.update:
update t_teacher
NAME = #{name, jdbcType=VARCHAR}, SEX = #{sex, jdbcType=NUMERIC}, BIRTHDAY = #{birthday, jdbcType=TIMESTAMP}, WORK_DATE = #{workDate, jdbcType=TIMESTAMP}, SPECIALTY = #{specialty, jdbcType=VARCHAR}, TLEVEL = #{tlevel, jdbcType=NUMERIC}, CREATE_DATE = #{createDate, jdbcType=TIMESTAMP}, ID = #{id}
ID = #{id}
