mapper.select:
select   from t_user


		and ID = #{User.id}


		and NAME = #{User.name}



		and BIRTHDAY >= #{birthdayBegin} 




		and BIRTHDAY <= #{birthdayEnd}



		and SEX = #{User.sex}


		and USERNAME = #{User.username}


		and PASSWORD = #{User.password}


		and LOGIN_FLAG = #{User.loginFlag}


		and ROLE_ID = #{User.roleId}


		and FILE_PATH = #{User.filePath}



		and CREATE_DATE >= #{createDateBegin} 




		and CREATE_DATE <= #{createDateEnd}




	order by ${order} ${sort}

mapper.update:
update t_user
NAME = #{name, jdbcType=VARCHAR}, BIRTHDAY = #{birthday, jdbcType=TIMESTAMP}, SEX = #{sex, jdbcType=NUMERIC}, USERNAME = #{username, jdbcType=VARCHAR}, PASSWORD = #{password, jdbcType=VARCHAR}, LOGIN_FLAG = #{loginFlag, jdbcType=NUMERIC}, ROLE_ID = #{roleId, jdbcType=NUMERIC}, FILE_PATH = #{filePath, jdbcType=VARCHAR}, CREATE_DATE = #{createDate, jdbcType=TIMESTAMP}, ID = #{id}
ID = #{id}
